import classNames from "classnames/bind";
import styles from "./SelectBox.module.scss";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { CiSearch } from "react-icons/ci";

const cx = classNames.bind(styles);

function SelectBox({
    startDate,
    endDate,
    type,
    keyword,
    setStartDate,
    setEndDate,
    setType,
    setKeyword,
    onSearch,
}) {
    return (
        <div className={cx("container_select-box")}>
            <div className={cx("item-1")}>

            </div>
            <div className={cx("filterRow")}>
                <select
                    className={cx("item-2")}
                    value={type}
                    onChange={e => setType(e.target.value)}
                    name="select-type"
                    id="type"
                >
                    <option value="door">Cửa</option>
                    <option value="dht">Cảm biến nhệt độ/độ ẩm</option>
                    <option value="gas">Cảm biến khí gas</option>
                </select>
                <div >
                    <DatePicker
                        selectsStart
                        selected={startDate}
                        onChange={(date) => setStartDate(date)}
                        startDate={startDate}
                        className={cx("item-2")}
                    />
                </div>
                <div >
                    <DatePicker
                        selectsEnd
                        selected={endDate}
                        onChange={(date) => setEndDate(date)}
                        endDate={endDate}
                        startDate={startDate}
                        minDate={startDate}
                        className={cx("item-2")}
                    />
                </div>

                <div >
                    <input
                        type="text"
                        onChange={(e) => setKeyword(e.target.value)}
                        value={keyword}
                        className={cx("item-2")}
                        placeholder="Từ khóa"
                    />
                </div>

                <div  onClick={onSearch}>
                    <CiSearch className={cx("icon")} style={{
                        cursor: 'pointer'
                    }}/>
                </div>
            </div>
        </div>
    );
}

export default SelectBox;
